package com.example.helloworld;

public class HelloWorld {

	// This is the starting code of the program
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		System.out.println("Hello World");
		
		// integer, number without .
		int age = 25;
		
		byte smallNumber = 10;
		
		short shortNumber = 30000;
		
		long largeNumber = 1000000000L;
		
		
		float pi = 3.14f;
		
		double distance = 1234.56;
		
		char grade = 'A';
		
		boolean isJavaFun = true;
		
		System.out.println("Integer value "+age );
		System.out.println("Byte value "+smallNumber );
		System.out.println("Short value "+shortNumber );
		System.out.println("Long value "+largeNumber );
		System.out.println("Float value value "+pi );
		System.out.println("Double value "+distance);
		System.out.println("Char value "+grade);
		System.out.println("Boolean value "+isJavaFun);
		
	

	}

}
